import { StatusBar } from 'expo-status-bar';
import { TouchableOpacity, Button, ImageBackground, StyleSheet, Text, TextInput, View, Platform } from 'react-native';



export default function App() {
  console.log(Platform);
  return (
    <View style={styles.container}>
      <ImageBackground
        source={require('./assets/images/background.png')}
        resizeMode='cover'
        style={styles.image}
      >
        <View style={styles.formContainer}>
          <Text style={styles.text}>Регистрация</Text>
        <TextInput
          placeholder={'Логин'}
        style={styles.input}/>
        <TextInput
          placeholder={'Адрес электронной почты'}
          style={styles.input} />
        <TextInput
            placeholder={'Пароль'}
            secureTextEntry={true}
            style={styles.input} />
          <TouchableOpacity
            activeOpacity={0.7}
            style={styles.button}>
            <Text style={styles.buttonText}>Зарегистрироваться</Text>
          </TouchableOpacity>
            <TouchableOpacity
            style={styles.login}>
            <Text style={styles.loginText}>Уже есть аккаунт? Войти</Text>
          </TouchableOpacity>
        </View>


      </ImageBackground>

      {/* <StatusBar style="auto" /> */}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  image: {
    flex: 1,
    justifyContent: 'flex-end',
  },
  formContainer: {

    backgroundColor: '#fff',
    borderTopRightRadius: 25, 
    borderTopLeftRadius: 25,
    padding: 16,
  },
  text: {
    textAlign: 'center',
    fontSize: 30,
    // fontStyle: 'bold',
    marginTop: 92,
    marginBottom: 33,
  },
  input: {
    borderWidth: 1,
    backgroundColor: '#F6F6F6',
    borderColor: '#E8E8E8',
    marginBottom: 16,
    borderRadius: 8,
    height: 50,
    color: '#212121',
    padding: 16,
    placeholderTextColor: '#BDBDBD',
  },
  button: {
    borderWidth: 1,
    marginTop: 27,
    borderRadius: 100,
    backgroundColor: '#FF6C00',
    borderColor: 'transparent',
    alignItems: 'center',
    padding: 16,
  },
  buttonText: {
    color: '#ffffff',
    fontSize: 16,
  },
  login: {
    alignItems: 'center',
    marginTop: 16,
    marginBottom: 66,
  },
  loginText: {
    color: '#1B4371',
    fontSize: 16,
  }
});
