import { StatusBar } from 'expo-status-bar';
import { useState } from 'react';
import { Keyboard, TouchableWithoutFeedback, KeyboardAvoidingView, TouchableOpacity, Button, ImageBackground, StyleSheet, Text, TextInput, View, Platform } from 'react-native';

const initialState = {
  login: '',
  email: '', 
  password: '',
  active: false
}
// const activeInitialState = {
//   login: false,
//   email: false, 
//   password: false
// }


export default function App() {
  // console.log(Platform.OS);
  const [keyboardShown, setKeyboardShown] = useState(false)
  const [state, setState] = useState(initialState)
  const [active, setActive] = useState({
    log: false,
    em: false, 
    pas: false})

  const hideKeyboard = () => {
    setKeyboardShown(false)
    Keyboard.dismiss()
    console.log(state)
    setState(initialState)
  }

  // const handleInputFocus = (value) => setActive((prevState) => ({ ...prevState, value: true }))

  // const handleInputBlur = () => setActive(false)

  return (
    <TouchableWithoutFeedback onPress={hideKeyboard}>
    <View style={styles.container}>

      <ImageBackground
        source={require('./assets/images/background.png')}
        // resizeMode='cover'
        style={styles.image}>



        <KeyboardAvoidingView
          behavior={Platform.OS === "ios" ? "padding" : "height"}
        >
          <View style={styles.formContainer}>
            <Text style={styles.text}>Регистрация</Text>
            <TextInput
                placeholder={'Логин'}
                // style={active.log ? ({...styles.input, borderColor: '#FF6C00'}) : ({...styles.input, borderColor: '#E8E8E8'})}
                // onFocus={setActive((prevState) => ({ ...prevState, login: true }))}
                // onBlur={handleInputBlur}
                value={state.login}
                onChangeText={(value) =>
                  setState((prevState) => ({ ...prevState, login: value }))}
            />
            <TextInput
              placeholder={'Адрес электронной почты'}
                // style={active.em ? ({...styles.input, borderColor: '#FF6C00'}) : ({...styles.input, borderColor: '#E8E8E8'})}
                // onFocus={setActive((prevState) => ({ ...prevState, email: true }))}
                // onBlur={handleInputBlur}
                value={state.email}
                onChangeText={(value) =>
                  setState((prevState) => ({ ...prevState, email: value }))}
            />
            <TextInput
              placeholder={'Пароль'}
              secureTextEntry={true}
                style={active.pas ? ({...styles.input, borderColor: '#FF6C00'}) : ({...styles.input, borderColor: '#E8E8E8'})}
                onFocus={setActive(() => ({pas: true}))}
                onBlur={setActive(() => ({pas: false}))}
                value={state.password}
                onChangeText={(value) =>
                  setState((prevState) => ({ ...prevState, password: value }))}
            /> 
            <TouchableOpacity
              activeOpacity={0.7}
              style={styles.button}>
              <Text style={styles.buttonText}>Зарегистрироваться</Text>
            </TouchableOpacity>
            <TouchableOpacity
              style={styles.login}>
              <Text style={styles.loginText}>Уже есть аккаунт? Войти</Text>
            </TouchableOpacity>
          </View>

      </KeyboardAvoidingView>


    </ImageBackground>

    </View>
    </TouchableWithoutFeedback>

  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  image: {
    flex: 1,
    justifyContent: 'flex-end',
  },
  formContainer: {

    backgroundColor: '#fff',
    borderTopRightRadius: 25, 
    borderTopLeftRadius: 25,
    padding: 16,
  },
  text: {
    textAlign: 'center',
    fontSize: 30,
    // fontStyle: 'bold',
    marginTop: 92,
    marginBottom: 33,
  },
  inputsGroup: {
    marginBottom: 32,
  },
  input: {
    borderWidth: 1,
    backgroundColor: '#F6F6F6',
    // borderColor: '#E8E8E8',
    marginBottom: 16,
    borderRadius: 8,
    height: 50,
    color: '#212121',
    padding: 16,
    placeholderTextColor: '#BDBDBD',
  },
  button: {
    borderWidth: 1,
    marginTop: 27,
    borderRadius: 100,
    backgroundColor: '#FF6C00',
    borderColor: 'transparent',
    alignItems: 'center',
    padding: 16,
  },
  buttonText: {
    color: '#ffffff',
    fontSize: 16,
  },
  login: {
    alignItems: 'center',
    marginTop: 16,
    marginBottom: 66,
  },
  loginText: {
    color: '#1B4371',
    fontSize: 16,
  }
});
